import Content from "./Content";
import "bootstrap/dist/css/bootstrap.min.css";
import Hub from "../components/Hub";
import Task from "../components/Task";
import What from "../components/What";
import Schedule from "../components/Schedule";
import { DarkMode } from "../App";
import { useContext } from "react";

const Home = () => {
  const { setIsDarkMode } = useContext(DarkMode);

  const styles = {
    body: {
      backgroundColor: isDarkMode ? "white" : "grey",
    },
  };
  return (
    <div className='box' style={styles.body}>
      <div className='url'>
        <Content
          title='Instant collaborations for remote teams'
          text='All in one for your remote team chats, collaboration and track projects'
          button='Get early access'
          placeholder='Email'
          type='email'
        />
      </div>
      <Hub />
      <Task />
      <Schedule />
      <What />
    </div>
  );
};

export default Home;
